/**
 * Get the squared distance between to points.
 *
 * (Avoid computing the square-root when unnecessary.)
 *
 * @param {Object} a in cartesian coordinates.
 * @param {Number} a.x
 * @param {Number} a.y
 * @param {Number} a.z
 * @param {Object} b in cartesian coordinates.
 * @param {Number} b.x
 * @param {Number} b.y
 * @param {Number} b.z
 * @returns {Number}
 */
export function distanceSquared(a, b) {
  const x = b.x - a.x;
  const y = b.y - a.y;
  const z = b.z - a.z;
  return x * x + y * y + z * z;
}

/**
 * Get the distance between to points.
 *
 * @param {Object} a in cartesian coordinates.
 * @param {Number} a.x
 * @param {Number} a.y
 * @param {Number} a.z
 * @param {Object} b in cartesian coordinates.
 * @param {Number} b.x
 * @param {Number} b.y
 * @param {Number} b.z
 * @returns {Number}
 */
export function distance(a, b) {
  return Math.sqrt(this.distanceSquared(a, b) );
}

export default {
  distance,
  distanceSquared,
  tree,
};

/**
 * @fileOverview SOFA convention to and from openGL convention.
 *
 * SOFA distances are in metres, angles in degrees.
 *
 * <pre>
 *
 * SOFA          +z  +x             openGL    +y
 *                | /                          |
 *                |/                           |
 *         +y ----o                            o---- +x
 *                                            /
 *                                           /
 *                                          +z
 *
 * SOFA.x = -openGL.z               openGL.x = -SOFA.y
 * SOFA.y = -openGL.x               openGL.y =  SOFA.z
 * SOFA.z =  openGL.y               openGL.z = -SOFA.x
 *
 * SOFA.azimuth = atan2(SOFA.y, SOFA.x)
 * SOFA.elevation = atan2(SOFA.z, sqrt(SOFA.x * SOFA.x + SOFA.y * SOFA.y) );
 * SOFA.distance = sqrt(SOFA.x * SOFA.x + SOFA.y * SOFA.y + SOFA.z * SOFA.z)
 *
 * </pre>
 *
 * @author Jean-Philippe.Lambert@ircam.fr
 * @copyright 2015-2016 IRCAM, Paris, France
 * @license BSD-3-Clause
 */

import degree from './degree';

/**
 * Coordinates as an array of 3 values:
 * [x, y, z] or [azimuth, elevation, distance], depending on type
 *
 * @typedef coordinates
 * @type {vec3}
 */

/**
 * Coordinates system type: sofaCartesian', 'sofaSpherical', or'gl'.
 *
 * @typedef coordinatesType
 * @type {String}
 */

/**
 * Convert SOFA cartesian coordinates to openGL.
 *
 * @param {coordinates} out in-place if out === a.
 * @param {coordinates} a
 * @returns {coordinates} out
 */
export function sofaCartesianToGl(out, a) {
  // copy to handle in-place
  const x = a[0];
  const y = a[1];
  const z = a[2];

  out[0] = -y;
  out[1] = z;
  out[2] = -x;

  return out;
}

/**
 * Convert openGL coordinates to SOFA cartesian.
 *
 * @param {coordinates} out in-place if out === a.
 * @param {coordinates} a
 * @returns {coordinates} out
 */
export function glToSofaCartesian(out, a) {
  // copy to handle in-place
  const x = a[0];
  const y = a[1];
  const z = a[2];

  out[0] = -z;
  out[1] = -x;
  out[2] = y;

  return out;
}

/**
 * Convert SOFA cartesian coordinates to SOFA spherical.
 *
 * @param {coordinates} out in-place if out === a.
 * @param {coordinates} a
 * @returns {coordinates} out
 */
export function sofaCartesianToSofaSpherical(out, a) {
  // copy to handle in-place
  const x = a[0];
  const y = a[1];
  const z = a[2];

  const x2y2 = x * x + y * y;

  // from [-180, 180] to [0, 360);
  out[0] = (degree.atan2(y, x) + 360) % 360;

  out[1] = degree.atan2(z, Math.sqrt(x2y2) );
  out[2] = Math.sqrt(x2y2 + z * z);

  return out;
}

/**
 * Convert SOFA spherical coordinates to SOFA spherical.
 *
 * @param {coordinates} out in-place if out === a.
 * @param {coordinates} a
 * @returns {coordinates} out
 */
export function sofaSphericalToSofaCartesian(out, a) {
  // copy to handle in-place
  const azimuth = a[0];
  const elevation = a[1];
  const distance = a[2];

  const cosE = degree.cos(elevation);
  out[0] = distance * cosE * degree.cos(azimuth); // SOFA.x
  out[1] = distance * cosE * degree.sin(azimuth); // SOFA.y
  out[2] = distance * degree.sin(elevation); // SOFA.z

  return out;
}

/**
 * Convert SOFA spherical coordinates to openGL.
 *
 * @param {coordinates} out in-place if out === a.
 * @param {coordinates} a
 * @returns {coordinates} out
 */
export function sofaSphericalToGl(out, a) {
  // copy to handle in-place
  const azimuth = a[0];
  const elevation = a[1];
  const distance = a[2];

  const cosE = degree.cos(elevation);
  out[0] = -distance * cosE * degree.sin(azimuth); // -SOFA.y
  out[1] = distance * degree.sin(elevation); // SOFA.z
  out[2] = -distance * cosE * degree.cos(azimuth); // -SOFA.x

  return out;
}

/**
 * Convert openGL coordinates to SOFA spherical.
 *
 * @param {coordinates} out in-place if out === a.
 * @param {coordinates} a
 * @returns {coordinates} out
 */
export function glToSofaSpherical(out, a) {
  // copy to handle in-place
  // difference to avoid generating -0 out of 0
  const x = 0 - a[2]; // -openGL.z
  const y = 0 - a[0]; // -openGL.x
  const z = a[1]; // openGL.y

  const x2y2 = x * x + y * y;

  // from [-180, 180] to [0, 360);
  out[0] = (degree.atan2(y, x) + 360) % 360;

  out[1] = degree.atan2(z, Math.sqrt(x2y2) );
  out[2] = Math.sqrt(x2y2 + z * z);

  return out;
}

/**
 * Convert typed coordinates to SOFA cartesian.
 *
 * @param {coordinates} out in-place if out === a.
 * @param {coordinates} a
 * @param {coordinatesType} type
 * @returns {coordinates} out
 * @throws {Error} when the type is unknown.
 */
export function typedToSofaCartesian(out, a, type) {
  switch (type) {
    case 'sofaCartesian':
      out[0] = a[0];
      out[1] = a[1];
      out[2] = a[2];
      break;

    case 'sofaSpherical':
      sofaSphericalToSofaCartesian(out, a);
      break;

    default:
      throw new Error('Bad SOFA type');
  }
  return out;
}

/**
 * Convert typed coordinates to openGL.
 *
 * @param {coordinates} out in-place if out === a.
 * @param {coordinates} a
 * @param {coordinatesType} type
 * @returns {coordinates} out
 * @throws {Error} when the type is unknown.
 */
export function typedToGl(out, a, type) {
  switch (type) {
    case 'gl':
      out[0] = a[0];
      out[1] = a[1];
      out[2] = a[2];
      break;

    case 'sofaCartesian':
      sofaCartesianToGl(out, a);
      break;

    case 'sofaSpherical':
      sofaSphericalToGl(out, a);
      break;

    default:
      throw new Error('Bad SOFA type');
  }
  return out;
}

/**
 * Convert openGL coordinates to typed ones.
 *
 * @param {coordinates} out in-place if out === a.
 * @param {coordinates} a
 * @param {coordinatesType} type
 * @returns {coordinates} out
 * @throws {Error} when the type is unknown.
 */
export function glToTyped(out, a, type) {
  switch (type) {
    case 'gl':
      out[0] = a[0];
      out[1] = a[1];
      out[2] = a[2];
      break;

    case 'sofaCartesian':
      glToSofaCartesian(out, a);
      break;

    case 'sofaSpherical':
      glToSofaSpherical(out, a);
      break;

    default:
      throw new Error('Bad SOFA type');
  }
  return out;
}

export default {
  sofaCartesianToGl,
  sofaCartesianToSofaSpherical,
  glToSofaCartesian,
  glToSofaSpherical,
  glToTyped,
  sofaSphericalToSofaCartesian,
  sofaSphericalToGl,
  typedToSofaCartesian,
  typedToGl,
};

/**
 * @fileOverview Convert to and from degree
 * @author Jean-Philippe.Lambert@ircam.fr
 * @copyright 2015-2016 IRCAM, Paris, France
 * @license BSD-3-Clause
 */

/**
 * Degree to radian multiplication factor.
 */
export const toRadianFactor = Math.PI / 180;

/**
 * Radian to degree multiplication factor.
 */
export const fromRadianFactor = 1 / toRadianFactor;

/**
 * Convert an angle in degrees to radians.
 *
 * @param {Number} angle in degrees
 * @returns {Number} angle in radians
 */
export function toRadian(angle) {
  return angle * toRadianFactor;
}

/**
 * Convert an angle in radians to degrees.
 *
 * @param {Number} angle in radians
 * @returns {Number} angle in degrees
 */
export function fromRadian(angle) {
  return angle * fromRadianFactor;
}

/**
 * Get the cosinus of an angle in degrees.
 *
 * @param {Number} angle
 * @returns {Number}
 */
export function cos(angle) {
  return Math.cos(angle * toRadianFactor);
}

/**
 * Get the sinus of an angle in degrees.
 *
 * @param {Number} angle
 * @returns {Number}
 */
export function sin(angle) {
  return Math.sin(angle * toRadianFactor );
}

/**
 * Get the arc-tangent (2 arguments) of 2 angles in degrees.
 *
 * @param {Number} y
 * @param {Number} x
 * @returns {Number}
 */
export function atan2(y, x) {
  return Math.atan2(y, x) * fromRadianFactor;
}

export default {
  atan2,
  cos,
  fromRadian,
  fromRadianFactor,
  sin,
  toRadian,
  toRadianFactor,
};

